import React, { createRef } from "react";
import "./Canvas.scss";

export default class Canvas extends React.Component {
  canvasRef = createRef();
  ctxRef = createRef();

  componentDidUpdate() {
    canvas = canvasRef.current
  }

  

  render() {

    
    return (
      <canvas
        ref={canvasRef}
        className="homepage__canvas"
      ></canvas>
    );
  }
}
