import React, { createRef } from "react";
import "./Canvas.scss";

export default class Canvas extends React.Component {
  canvasRef = createRef();
  render() {
   
    console.log(canvasRef)
    let c = canvasRef.current
    let ctx = c.getContext("2d");

    return (
      <canvas
        ref={canvasRef}
        className="homepage__canvas"
      ></canvas>
    );
  }
}
