import React from "react";
import "./Canvas.scss";

export default function Canvas(props) {
  return (
    <canvas
      ref={props.ref}
      onMouseDown={props.startDraw}
      onMouseUp={props.endDraw}
      onMouseMove={props.draw}
      className="homepage__canvas"
    ></canvas>
  );
}
